import { Dashboard, Logout, Settings } from '@mui/icons-material';
import { ListItemIcon, Menu, MenuItem } from '@mui/material';
import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useValue } from '../../context/ContextProvider';

const UserMenu=({anchorUserMenu,setAnchorUserMenu})=>{
    const{dispatch,state:{currentUser}}=useValue();
    const handleCloseUserMenu = () => {
        setAnchorUserMenu(null);
      };

    const testAuthorization=async()=>{
        const url=process.env.REACT_APP_SERVER_URL + '/room'
        try{
            const response =await fetch(url,{
                method:'POST',
                headers:{
                    'Content-Type':'application/json',
                    authorization:`Bearer ${currentUser.token}`
                }
            })
            const data=await response.json()
            console.log(data);
            if(!data.success){
                throw new Error(data.message)
            }

        }catch(error){
            dispatch({type:'UPDATE_ALERT',payload:{open:true, severity:'error',message:error.message}})
            console.log(error);
        }
    }
    return (
        <Menu
            anchorEl={anchorUserMenu}
            open={Boolean(anchorUserMenu)}
            onClose={handleCloseUserMenu}
            onClick={handleCloseUserMenu}
        >
            <MenuItem onClick={testAuthorization}>
                <ListItemIcon>
                    <Settings fontSize="small"/>

                </ListItemIcon>
                Profile
            </MenuItem>

            <MenuItem onClick={()=>dispatch({type:'UPDATE_USER',playload:null})}>
                <ListItemIcon>
                    <Logout fontSize="small"/>

                </ListItemIcon>
                Logout
            </MenuItem>
        </Menu>
    )
}

export default UserMenu
